$.ajax({
    method: 'GET',
    url: 'http://127.0.0.1:8000/api/notifications',
    success: function(x) {
        if (x.length > 0) {
            document.getElementById('badge').innerHTML = x.length
            for (let i = 0; i < x.length; i++) {
                const e = x[i];
                console.log(e.data.payment);

                $(`<a class="d-flex align-items-center dropdown-item" href="#">
                <div class="mr-3">
                    <div class="bg-danger icon-circle"><i class="fas fa-exclamation-triangle text-white"></i></div>
                </div>
                <div><span class="small text-gray-500">December 2, 2019</span>
                    <p>Spending Alert: We&#39;ve noticed unusually high spending for your account.</p>
                </div>
            </a>`).appendTo('#notifications');

            }
        }

    }
})
